import React from 'react';
import logo from './logo.svg';
import './App.css';

var moment = require('moment');

var startOfWeek = moment().startOf('isoweek').format("YYYY-MM-DD");
var endOfWeek   = moment().endOf('isoweek').format("YYYY-MM-DD");

class Calendar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        data:[],
        isdataSet : false,
    }
    fetch("https://back.staging.bsport.io/api/v1/offer/?company=6&min_date="+startOfWeek + "&max_date="+endOfWeek)
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({"data": result["results"], "isdataSet":true});
            });

  }
   render() {
       const listItems = this.state.data.map((entry) =>
           <li>{entry.activity}</li> );

       if(this.state.isdataSet){
          return {listItems};
      }

  }
}


function App() {
  return (
    <div className="App">
      <Calendar/>
    </div>
  );
}

export default App;
