import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'moment/locale/fr'  // without this line it didn't work

var moment = require('moment');
moment.locale('fr');         // fr

var startOfWeek = moment().startOf('isoweek').format("YYYY-MM-DD");
var endOfWeek   = moment().endOf('isoweek').format("YYYY-MM-DD");


class List_ele extends React.Component{ //display list of events from props.data
    render() {
        return (
            <ul className="list-group">
                {this.props.data.map(entry => <li key={entry.id} className="list-group-item">
                    <h6>Date : {moment(entry.date_start).calendar(moment())}</h6>
                    <h7>Durée en minute : {moment(entry.duration_minute).calendar(moment())}</h7>
                    <br></br>
                    <small>Coach : {entry.coach}</small><small> | Activité : {entry.activity}</small><small> |
                    Établissement : {entry.establishment}</small>

                </li>)}
            </ul>
        );
    }
}
class Calendar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        data:[],
        isdataSet : false,
    }
    fetch("https://back.staging.bsport.io/api/v1/offer/?company=6&min_date="+startOfWeek + "&max_date="+endOfWeek)
        .then(res => res.json())
        .then(
            (result) => {
                var res = new Array();
                result["results"].forEach((item) => res.push(item));
                this.setState({"data": res, "isdataSet":true});
            });

  }
   render() {
       if(this.state.isdataSet){
           return (
               <div className="container">
                   <div className="row">
                       <div class="col-6">
                           <h2>Séléctionner un jour pour voir les séances correspondantes</h2>
                           <Filter value={"Lundi"} key={1}></Filter>
                           <Filter value={"Mardi"} key={2}></Filter>
                           <Filter value={"Mercredi"} key={3}></Filter>
                           <Filter value={"Jeudi"} key={4}></Filter>
                           <Filter value={"Venredi"} key={5}></Filter>
                           <Filter value={"Samedi"} key={6}></Filter>
                           <Filter value={"Dimanche"} key={7}></Filter>

                       </div>
                       <div className="col-6">
                           <List_ele data={this.state.data}/>
                       </div>

                   </div>
               </div>
           );
      }
       else return "Loading...";

  }

}
class Filter extends React.Component{

    render() {
        return (
            <button type="button" className="btn btn-outline-primary" key={this.props.key}>{this.props.value}</button>
        );
    }
}


export default Calendar;
