import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'moment/locale/fr'  // without this line it didn't work

var moment = require('moment');
moment.locale('fr');         // fr

var startOfWeek = moment().startOf('isoweek').format("YYYY-MM-DD");
var endOfWeek   = moment().endOf('isoweek').format("YYYY-MM-DD");

class Calendar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        data:[],
        isdataSet : false,
    }
    fetch("https://back.staging.bsport.io/api/v1/offer/?company=6&min_date="+startOfWeek + "&max_date="+endOfWeek)
        .then(res => res.json())
        .then(
            (result) => {
                var res = new Array();
                result["results"].forEach((item) => res.push(item));
                this.setState({"data": res, "isdataSet":true});
            });

  }
   render() {


       if(this.state.isdataSet){
           return (
               <ul class="list-group">
               {this.state.data.map(entry => <li key={entry.id} class="list-group-item">
                  <h5>Date : {moment(entry.date_start).calendar(moment())}</h5>
                   <h6>Coach : {entry.coach}</h6><h6>Activité : {entry.activity}</h6><h6>Établissement : {entry.establishment}</h6>

               </li>)}
               </ul>
           )
      }
       else return "Loading...";

  }
}


function App() {
  return (
    <div class="container">
        <div class="row justify-content-center">
        <div class="col-6">
            <h2>Séances de la semaine actuelle : </h2>
            <Calendar/>
        </div>

        </div>
    </div>
  );
}

export default App;
