import React from 'react';
import logo from './logo.svg';
import './App.css';

var moment = require('moment');

var startOfWeek = moment().startOf('isoweek').format("YYYY-MM-DD");
var endOfWeek   = moment().endOf('isoweek').format("YYYY-MM-DD");

class Calendar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        data:[],
        isdataSet : false,
    }
    fetch("https://back.staging.bsport.io/api/v1/offer/?company=6&min_date="+startOfWeek + "&max_date="+endOfWeek)
        .then(res => res.json())
        .then(
            (result) => {
                var res = new Array();
                result["results"].forEach((item) => res.push(item));
                this.setState({"data": res, "isdataSet":true});
            });

  }
   render() {


       if(this.state.isdataSet){
           return (
               <ul class="list-group">
               {this.state.data.map(entry => <li key={entry.id} class="list-group-item">{entry.company}</li>)}
               </ul>
           )
      }
       else return "Loading...";

  }
}


function App() {
  return (
    <div class="container">
        <div class="row justify-content-center">
        <div class="col-3">
        </div>
        <div class="col-6">
            <Calendar/>
        </div>
        <div class="col-3">
        </div>
        </div>
    </div>
  );
}

export default App;
